#!/usr/bin/perl

@r = `find ../include/astro -name "*.h" -exec grep "define __peyton" {} \\; | uniq | awk '{print \$2}'`;

print
"//
//     !DO NOT EDIT! - file generated by mkuseall.pl - !DO NOT EDIT!
//
//   useall.h header - makes all included libpeyton namespace functions
//	available in global namespace (through using keyword)
//
";

foreach(@r)
{
	chomp;
	print "
#ifdef $_
	using namespace $_;
	#undef $_
#endif
";
}
